addEventListener("DOMContentLoaded",e=>{let o=document.querySelectorAll(".offer-info__item"),r=document.querySelector(".offer-button");o.forEach(t=>{t.addEventListener("click",function(){var e=t.getAttribute("data-href");r.setAttribute("href",e),o.forEach(e=>e.classList.remove("offer-info__item_active")),this.classList.add("offer-info__item_active")})})});